const SimplePlanSwitch = styled(Switch)(({ theme }) => ({
  width: 400,
  height: 50,
  padding: 2,

  "& .MuiSwitch-switchBase": {
    padding: 0,
    transform: "translateX(10px)",
    transition: "1s ease",

    "&.Mui-checked": {
      transform: "translateX(205px)",
      "& .MuiSwitch-thumb": {
        // background: "navy",
        // background:
        //   "linear-gradient(49deg, rgba(252,242,24,1) 16%, rgba(255,180,110,1) 100%)",
      },

      "& .MuiSwitch-thumb:before": {
        content: "'Individual '",
      },
      "& + .MuiSwitch-track": {
        opacity: 1,
        background: "whitesmoke",
        // background:
        //   theme.palette.mode === "dark"
        //     ? "red"
        //     : "linear-gradient(49deg, rgba(252,242,24,1) 0%, rgba(255,255,255,1) 51%, rgba(245,245,248,1) 56%, rgba(69,248,255,1) 100%)",
      },
    },
  },
  "& .MuiTouchRipple-root": {
    display: "inline-block",
    height: 30,
    top: 11,
    borderRadius: "5rem",
    width: 185,
    mt: "0.1rem",
  },
  "& .MuiSwitch-thumb": {
    // background:
    //   "linear-gradient(49deg, rgba(110,162,255,1) 5%,rgba(164,255,176,1) 80% )",
    // background: "red",
    outline: "2px solid whitesmoke",
    outlineOffset: "-2px",
    width: 185,
    borderRadius: "5rem",
    height: 30,
    display: "inline-block",
    marginTop: "0.6rem",
    boxShadow: 3,
    background: "rgb(179, 235, 255)",
    "&:before": {
      marginTop: "0.1rem",
      content: "'Group'",
      display: "flex",
      fontSize: "1.1rem",

      color: "black",
      justifyContent: "center",
      alignItems: "center",
      position: "absolute",
      width: "100%",
      height: "100%",

      left: 0,
      top: 0,
    },
  },
  "& .MuiSwitch-track": {
    opacity: 1,
    height: 47,
    transition: "1s ease",
    "&:before": {
      content: "'Group'",
      position: "absolute",
      left: "15%",
      top: 12,
      mt: "0.1rem",
      fontSize: "1.1rem",
      color: "grey",
    },
    "&:after": {
      content: "'Individual'",
      position: "absolute",
      right: "13%",
      top: 12,
      fontSize: "1.1rem",
      color: "grey",
    },
    width: 395,
    // backgroundColor: theme.palette.mode === "dark" ? "red" : "grey",

    // boxShadow: "inset 0px 0px 2px 1px #242003",
    background: "whitesmoke",
    // background:
    //   theme.palette.mode === "dark"
    //     ? "red"
    //     : "linear-gradient(49deg, rgba(252,242,24,1) 0%, rgba(255,255,255,1) 51%, rgba(245,245,248,1) 56%, rgba(69,248,255,1) 100%)",
    borderRadius: 66 / 2,
  },
}));
function SwitchLayout(props) {
  const {
    planFrontend,
    planFrontendStatus,
    title,
    planFrontend1,
    planFrontendStatus1,
    containerClass,
  } = props;
  const [checked, setChecked] = React.useState(false);
  const handleChange = (event) => {
    setChecked(event.target.checked);
    // checked === true ? setToggle(1) : setToggle(0);
  };
  const { i18n } = useTranslation();

  // Get the current language
  const currentLanguage = i18n.language;

  localStorage.setItem("backToPath", "/");

  //custom css and responsive value
  const hStyle = {
    fontSize: {
      xs: "16px",
      sm: "17px",
      md: "1.7rem",
    },
    fontWeight: 600,
    pt: "1rem",
    pb: "1rem",
    textAlign: "center",
  };

  return (
    <>
      <ThemeProvider theme={theme}>
        {/* subscripiont box start */}
        <Box
          pb={5}
          sx={{
            width: {
              md: "95%",
              xl: "80%",
            },
            mx: "auto",
            
          }}
        >
          {/* <subscription title */}

          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              border: "1px solid gray",
              flexDirection: "column",

              padding: "10px",
              alignItems: "center",
              width: "45%",
              borderRadius: "2rem",
              boxShadow: "inset 0px 0px 2px 1px #242003",
              backgroundColor: "rgb(227,242,253)",
              margin: "2rem auto",
            }}
          >
            <Typography variant="h3" sx={{ ...hStyle, color: "#000" }}>
              {title}
            </Typography>
            <SimplePlanSwitch
              checked={checked}
              onChange={handleChange}
              sx={{
                transform: {
                  xs: "scale(0.7)",
                  sm: "scale(1)",
                },
                display: {
                  xs: "none",
                  md: "block",
                },
              }}
            />
            <Box
              sx={{
                display: {
                  xs: "block",
                  md: "none",
                },
              }}
            >
              <Typography sx={{ textAlign: "center" }}>
                {checked ? "Individual" : "Group"}
              </Typography>
              <Switch checked={checked} onChange={handleChange} name="" />
            </Box>
          </Box>

          {!checked && (
            <SubscriptionPlanCard
              planFrontend={planFrontend}
              planFrontendStatus={planFrontendStatus}
              containerClass={containerClass}
              // planType={1}
            ></SubscriptionPlanCard>
          )}

          {checked &&
            (title === "Subscription" ? (
              <Box sx={{ my: 2 }}>
                <FullWidthTabs></FullWidthTabs>
              </Box>
            ) : (
              <SubscriptionPlanCard
                planFrontend={planFrontend1}
                planFrontendStatus={planFrontendStatus1}
                // planType={2}
              ></SubscriptionPlanCard>
            ))}
        </Box>
        {/* subscripiont box end */}
      </ThemeProvider>
    </>
  );
}

export default SwitchLayout;
